    function eliminar($imagenprod_id)
    {
      //  if($this->acceso(69)){
        $imagen_producto = $this->Imagen_asociado_model->get_imagen_asociado($imagenprod_id);

        // check if the imagen_producto exists before trying to delete it
        if(isset($imagen_producto['imagenasoc_id']))
        {
            $imagen= $this->Imagen_asociado_model->get_imagen_asociado($imagenprod_id);
            $base_url = explode('/', base_url());
            //$directorio = FCPATH.'resources\images\clientes\\';
            $directorio = $_SERVER['DOCUMENT_ROOT'].'/'.$base_url[3].'/resources/images/asociado/';
            //$directorio = $_SERVER['DOCUMENT_ROOT'].'/ximpleman_web/resources/images/clientes/';
            $foto1 = $imagen['imagenasoc_archivo'];
            if(isset($foto1) && !empty($foto1)){
              if(file_exists($directorio.$foto1)){
                  unlink($directorio.$foto1);
                  $mimagenthumb = "thumb_".$foto1;
                  unlink($directorio.$mimagenthumb);
                }
            }
            $this->Imagen_asociado_model->delete_imagen($imagenprod_id);
            redirect('imagen_asociado/catalogoasoc/'.$imagen_producto['asociado_id']);
        }
        else
            show_error('The imagen_producto you are trying to delete does not exist.');
       // }
    }
    
